mapper.select:
        SELECT *
          FROM (SELECT /* pahalfclaim.xml: pahalf.claim.selectCalimDoFlag by PaHalfClaimController*/ 
		               TO_NUMBER(DT.DO_FLAG) AS DO_FLAG
		          FROM TPAORDERM OM
		             , TCLAIMDT  DT
		         WHERE OM.ORDER_NO     = DT.ORDER_NO
		           AND OM.ORDER_G_SEQ  = DT.ORDER_G_SEQ
		           AND OM.ORDER_D_SEQ  = DT.ORDER_D_SEQ
		           AND OM.ORDER_W_SEQ  = DT.ORDER_W_SEQ
		           AND OM.PA_ORDER_NO  = #{ordNo			, jdbcType=VARCHAR}
		           AND OM.PA_ORDER_SEQ = #{ordNoNm			, jdbcType=VARCHAR}
		           AND OM.PA_CODE      = #{paCode   		, jdbcType=VARCHAR} 
		           AND OM.PA_SHIP_NO   = #{basketNo     	, jdbcType=VARCHAR} 
		           AND OM.PA_ORDER_GB  = #{orgPaOrderGb		, jdbcType=VARCHAR}
		           ORDER BY OM.MAPPING_SEQ DESC)
         WHERE ROWNUM = 1

        SELECT NVL((SELECT MAX(PO.CLAIM_NO)  /* pahalfclaim.xml: pahalf.claim.selectClaimNo by PaHalfClaimController*/ 
				      FROM TPAHALFORDERLIST PO
				     WHERE PO.ORD_NO        = #{ordNo  		,  jdbcType=VARCHAR}
				       AND PO.ORD_NO_NM     = #{ordNoNm 	,  jdbcType=VARCHAR}
				       AND PO.BASKET_NO     = #{basketNo    ,  jdbcType=VARCHAR}
				       AND PO.PA_ORDER_GB	= '40') , '000') AS  CLAIM_NO 
		  FROM DUAL

        SELECT XX.CLAIM_NO /* pahalfclaim.xml: pahalf.claim.selectClaimNo4ClaimCancel by PaHalfClaimController*/ 
          FROM( SELECT PO.CLAIM_NO
				  FROM TPAHALFORDERLIST PO
				 WHERE PO.ORD_NO        = #{ordNo  			,  jdbcType=VARCHAR}
				   AND PO.ORD_NO_NM     = #{ordNoNm 		,  jdbcType=VARCHAR}
				   AND PO.BASKET_NO     = #{basketNo    	,  jdbcType=VARCHAR}
				   AND PO.PA_ORDER_GB	= #{paOrderGb		,  jdbcType=VARCHAR}
				 ORDER BY PO.CLAIM_REQUEST_DATE DESC ) XX
		 WHERE ROWNUM = 1

SELECT /* pahalfclaim.xml : pahalf.claim.selectClaimCancelTargetDt by PaHalfAsyncController*/ PM.MAPPING_SEQ , OPM.ORDER_NO , OPM.ORDER_G_SEQ , OPM.ORDER_D_SEQ , OPM.ORDER_W_SEQ , PM.PA_ORDER_GB AS CLAIM_GB , PM.PA_PROC_QTY AS CLAIM_QTY , '' AS CLAIM_DESC , '' AS OUT_BEF_CLAIM_GB , OPM.PRE_CANCEL_YN , PM.PA_ORDER_GB , '0' AS ADMIN_PROC_YN , '' AS RETURN_NAME , '' AS RETURN_TEL , '' AS RETURN_HP , '' AS RETURN_ADDR , '' AS RCVR_MAIL_NO , '' AS RCVR_MAIL_NO_SEQ , '' AS RCVR_BASE_ADDR , '' AS RCVR_DTLS_ADDR , '' AS RCVR_TYPE_ADD , NVL( (SELECT A.CS_LMS_CODE FROM TCSKINDS A WHERE A.CS_LMS_CODE = '6312' || PCC.CS_SGROUP) , '631299') AS CLAIM_CODE , 0 AS CLM_LST_DLV_CST , TC.CODE_SNAME AS INSERT_ID FROM TPAORDERM PM /*반품취소건*/ , TPAORDERM OPM /*원 반품건*/ , TPAHALFORDERLIST PC , TCODE TC , TPACLAIMCODE PCC WHERE PM.PA_ORDER_NO = OPM.PA_ORDER_NO AND PM.PA_ORDER_SEQ = OPM.PA_ORDER_SEQ AND PM.PA_CODE = OPM.PA_CODE AND PM.PA_SHIP_NO = OPM.PA_SHIP_NO AND PM.PA_CLAIM_NO = OPM.PA_CLAIM_NO AND TC.CODE_LGROUP = 'O500' AND TC.CODE_MGROUP = PM.PA_GROUP_CODE AND PM.PA_ORDER_GB = '31' AND PM.PA_CODE IN ('C1', 'C2') AND PM.OUT_BEF_CLAIM_GB = '0' AND PM.CREATE_YN = '0' AND PM.PRE_CANCEL_YN = '0' AND PM.INSERT_DATE > TRUNC(SYSDATE) - 30 AND OPM.PA_ORDER_GB = '30' AND OPM.CREATE_YN = '1' AND PM.PA_ORDER_NO = PC.ORD_NO AND PM.PA_ORDER_SEQ = PC.ORD_NO_NM AND PM.PA_SHIP_NO = PC.BASKET_NO AND PM.PA_CLAIM_NO = PC.CLAIM_NO AND PM.PA_ORDER_GB = PC.PA_ORDER_GB AND PC.REFUND_STATE = PCC.PA_CLAIM_CODE(+) AND PCC.CLAIM_GB(+) = '30' AND PCC.PA_CODE(+) = '12' AND PM.PA_CODE = #{paCode , jdbcType=VARCHAR} AND PM.PA_ORDER_NO = #{paOrderNo , jdbcType=VARCHAR} AND PM.PA_ORDER_SEQ = #{paOrderSeq , jdbcType=VARCHAR} AND PM.PA_CLAIM_NO = #{paClaimNo , jdbcType=VARCHAR}
UNION ALL
SELECT PM.MAPPING_SEQ , '' AS ORDER_NO , '' AS ORDER_G_SEQ , '' AS ORDER_D_SEQ , '' AS ORDER_W_SEQ , PM.PA_ORDER_GB , '' AS PA_PROC_QTY , '' AS CLAIM_DESC , '' AS OUT_BEF_CLAIM_GB , '1' AS PRE_CANCEL_YN , PM.PA_ORDER_GB , '' AS ADMIN_PROC_YN , '' AS RETURN_NAME , '' AS RETURN_TEL , '' AS RETURN_HP , '' AS RETURN_ADDR , '' AS RCVR_MAIL_NO , '' AS RCVR_MAIL_NO_SEQ , '' AS RCVR_BASE_ADDR , '' AS RCVR_DTLS_ADDR , '' AS RCVR_TYPE_ADD , '' AS CLAIM_CODE , 0 AS CLM_LST_DLV_CST , TC.CODE_SNAME AS SITE_GB FROM TPAORDERM PM , TCODE TC WHERE PM.PA_ORDER_GB = '31' AND PM.CREATE_YN = '0' AND PM.PRE_CANCEL_YN = '0' AND TC.CODE_LGROUP = 'O500' AND PM.PA_CODE IN ('C1', 'C2') AND TC.CODE_MGROUP = PM.PA_GROUP_CODE AND PM.PA_CODE = #{paCode , jdbcType=VARCHAR} AND PM.PA_ORDER_NO = #{paOrderNo , jdbcType=VARCHAR} AND PM.PA_ORDER_SEQ = #{paOrderSeq , jdbcType=VARCHAR} AND PM.PA_CLAIM_NO = #{paClaimNo , jdbcType=VARCHAR} AND NOT EXISTS (SELECT 'x' FROM TPAHALFORDERLIST CL WHERE PM.PA_ORDER_NO = CL.ORD_NO AND PM.PA_ORDER_SEQ = CL.ORD_NO_NM AND PM.PA_SHIP_NO = CL.BASKET_NO AND CL.PA_ORDER_GB = '30')

SELECT /* pahalfclaim.xml : pahalf.claim.selectChangeCancelTargetDtList by PaHalfAsyncController*/ MAPPING_SEQ , CLAIM_GB , ORDER_NO , ORDER_G_SEQ , ORDER_D_SEQ , ORDER_W_SEQ , CLAIM_QTY , CLAIM_CODE , PRE_CANCEL_YN , CLM_LST_DLV_CST , SITE_GB , OUT_BEF_CLAIM_GB , PA_CODE , PA_ORDER_NO , PA_ORDER_SEQ , PA_SHIP_SEQ , INSERT_ID FROM (SELECT PM.MAPPING_SEQ , PM.PA_ORDER_GB AS CLAIM_GB , OPM.ORDER_NO , OPM.ORDER_G_SEQ , OPM.ORDER_D_SEQ , OPM.ORDER_W_SEQ , PM.PA_PROC_QTY AS CLAIM_QTY , '999' AS CLAIM_CODE , OPM.PRE_CANCEL_YN , 0 AS CLM_LST_DLV_CST , TC.CODE_SNAME AS SITE_GB , PM.OUT_BEF_CLAIM_GB , PM.PA_CODE , PM.PA_ORDER_NO , PM.PA_ORDER_SEQ , PM.PA_SHIP_SEQ , TC.CODE_SNAME AS INSERT_ID FROM TPAORDERM PM /* 교환취소건 */ , TPAORDERM OPM /* 원교환배송 건 */ , TCODE TC WHERE PM.PA_ORDER_NO = OPM.PA_ORDER_NO AND PM.PA_ORDER_SEQ = OPM.PA_ORDER_SEQ AND PM.PA_CODE = OPM.PA_CODE AND PM.PA_SHIP_NO = OPM.PA_SHIP_NO AND PM.PA_CLAIM_NO = OPM.PA_CLAIM_NO AND PM.PA_ORDER_GB = DECODE(OPM.PA_ORDER_GB , '40','41','46') AND TC.CODE_LGROUP = 'O500' AND TC.CODE_MGROUP = PM.PA_GROUP_CODE AND PM.PA_CODE IN ('C1', 'C2') AND PM.PA_ORDER_GB IN ('41', '46') AND OPM.PA_ORDER_GB IN ('40', '45') AND OPM.CREATE_YN = '1' AND PM.CREATE_YN = '0' AND PM.PRE_CANCEL_YN = '0' AND PM.PA_ORDER_NO = #{paOrderNo , jdbcType=VARCHAR} AND PM.PA_ORDER_SEQ = #{paOrderSeq , jdbcType=VARCHAR} AND PM.PA_CLAIM_NO = #{paClaimNo , jdbcType=VARCHAR}
UNION ALL
SELECT PM.MAPPING_SEQ , PM.PA_ORDER_GB , '' AS ORDER_NO , '' AS ORDER_G_SEQ , '' AS ORDER_D_SEQ , '' AS ORDER_W_SEQ , '' AS CLAIM_QTY , '' AS CLAIM_CODE , '1' AS PRE_CANCEL_YN , 0 AS CLM_LST_DLV_CST , TC.CODE_SNAME AS SITE_GB , '0' AS OUT_BEF_CLAIM_GB , PM.PA_CODE , PM.PA_ORDER_NO , PM.PA_ORDER_SEQ , PM.PA_SHIP_SEQ , TC.CODE_SNAME FROM TPAORDERM PM /* 교환취소건 */ , TCODE TC WHERE PM.PA_CODE IN ('C1', 'C2') AND PM.CREATE_YN = '0' AND PM.PRE_CANCEL_YN = '0' AND PM.PA_ORDER_GB IN ('41', '46') AND TC.CODE_LGROUP = 'O500' AND TC.CODE_MGROUP = PM.PA_GROUP_CODE AND PM.PA_ORDER_NO = #{paOrderNo , jdbcType=VARCHAR} AND PM.PA_ORDER_SEQ = #{paOrderSeq , jdbcType=VARCHAR} AND PM.PA_CLAIM_NO = #{paClaimNo , jdbcType=VARCHAR} AND NOT EXISTS ( SELECT 'x' FROM TPAORDERM OPM WHERE OPM.PA_ORDER_NO = PM.PA_ORDER_NO AND OPM.PA_ORDER_SEQ = PM.PA_ORDER_SEQ AND OPM.PA_SHIP_NO = PM.PA_SHIP_NO AND OPM.PA_CODE = PM.PA_CODE AND OPM.PA_ORDER_GB IN ('40','45') AND OPM.CREATE_YN = '1') ) ORDER BY CLAIM_GB ASC

mapper.insert:
     	 MERGE  /* pahalfclaim.xml : pahalf.claim.mergeExchangeOrderNm by PaHalfClaimController*/ 
     	 	   INTO TPAHALFORDERLIST USING DUAL ON ( ORD_NO 			= #{ordNo		,	jdbcType=VARCHAR}
								           		 AND ORD_NO_NM 			= #{ordNoNm		,	jdbcType=VARCHAR}
								           		 AND BASKET_NO 			= #{basketNo	,	jdbcType=VARCHAR}
								           	 	 AND CLAIM_REQUEST_DATE = TO_CHAR(#{claimRequestDate ,  jdbcType=TIMESTAMP} , 'YYYYMMDDHH24MISS')
								           		 AND PA_ORDER_GB		= '40'
								           		 )
		       WHEN MATCHED THEN
		       UPDATE SET EXCHANGE_ORD_NO = #{ exchangeOrdNo ,	jdbcType=VARCHAR}

mapper.select:
     	SELECT XX.PA_CLAIM_NO
		  FROM (SELECT A.PA_CLAIM_NO
		          FROM TPAORDERM A
		         WHERE A.PA_ORDER_GB 	= '30'
		           AND A.PA_ORDER_NO 	= #{ordNo		,	jdbcType=VARCHAR}
		           AND A.PA_CODE 		= #{paCode		,	jdbcType=VARCHAR}
		           AND A.PA_ORDER_SEQ 	= #{ordNoNm		,	jdbcType=VARCHAR}
		           AND A.PA_SHIP_NO   	= #{basketNo	,	jdbcType=VARCHAR}
		          ORDER BY A.MAPPING_SEQ DESC ) XX
 		 WHERE ROWNUM = 1
