mapper.insert:
MERGE INTO TPAHALFBRAND A  /* pahalfcommon.xml : pahalfcommon.insertPaHalfBrand by PaHalfCommonController*/
USING DUAL
ON
  (
     A.PA_BRAND_NO = #{paBrandNo, jdbcType=VARCHAR}
  )
WHEN MATCHED THEN
	 UPDATE SET
	     A.PA_GROUP_CODE  	  = #{paGroupCode     , jdbcType=VARCHAR}
       , A.PA_CODE        	  = #{paCode          , jdbcType=VARCHAR}
       , A.PA_BRAND_NAME  	  = #{paBrandName	  , jdbcType=VARCHAR}
       , A.USE_YN         	  = #{useYn			  , jdbcType=VARCHAR}
       , A.MODIFY_ID      	  = #{modifyId		  , jdbcType=VARCHAR}
       , A.MODIFY_DATE    	  = SYSDATE
       , A.RETURN_NO_YN   	  = #{returnNoYn	  , jdbcType=VARCHAR}
       , A.PA_DETAIL_BRAND_NO = #{paDetailBrandNo , jdbcType=VARCHAR}

WHEN NOT MATCHED THEN
     INSERT 
     ( 
     	 A.PA_GROUP_CODE
       , A.PA_CODE
       , A.PA_BRAND_NO
       , A.PA_BRAND_NAME
       , A.USE_YN
       , A.INSERT_ID
       , A.INSERT_DATE
       , A.MODIFY_ID
       , A.MODIFY_DATE 
       , A.RETURN_NO_YN
       , A.PA_DETAIL_BRAND_NO
     )
VALUES 
     ( 
       #{paGroupCode    , jdbcType=VARCHAR}
     , #{paCode	        , jdbcType=VARCHAR}
     , #{paBrandNo		, jdbcType=VARCHAR}
     , #{paBrandName	, jdbcType=VARCHAR}
     , #{useYn			, jdbcType=VARCHAR}
     , #{insertId		, jdbcType=VARCHAR}
     , SYSDATE
     , #{modifyId		, jdbcType=VARCHAR}
     , SYSDATE
     , #{returnNoYn		, jdbcType=VARCHAR}
     , #{paDetailBrandNo , jdbcType=VARCHAR}
     )

        INSERT INTO /* pahalfcommon.xml : pahalfcommon.insertPaHalfOfferCode by PaHalfCommonController */
               		TPAOFFERCODE
		           ( PA_GROUP_CODE
		           , PA_OFFER_TYPE  
		           , PA_OFFER_CODE 
		           , PA_OFFER_TYPE_NAME 
		           , PA_OFFER_CODE_NAME
		           , REQUIRED_YN
		           , SORT_SEQ
		           , USE_YN
		           , REMARK
		           , UNIT_REQUIRED_YN
		           , IPT_MTHD_CD
	               , INSERT_ID
		           , INSERT_DATE
	               , MODIFY_ID
	               , MODIFY_DATE )
		     VALUES 
		           ( #{paGroupCode		, jdbcType=VARCHAR}
		           , #{paOfferType		, jdbcType=VARCHAR}
		           , #{paOfferCode		, jdbcType=VARCHAR}
	               , #{paOfferTypeName	, jdbcType=VARCHAR}
		           , #{paOfferCodeName	, jdbcType=VARCHAR}
		           , #{requiredYn		, jdbcType=VARCHAR}
		           , #{sortSeq			, jdbcType=VARCHAR}
		           , #{useYn			, jdbcType=VARCHAR}
	               , #{remark			, jdbcType=VARCHAR}
	               , #{unitRequiredYn	, jdbcType=VARCHAR}
	               , #{iptMthdCd		, jdbcType=VARCHAR}
	               , #{insertId			, jdbcType=VARCHAR}
	               , SYSDATE
	               , #{modifyId			, jdbcType=VARCHAR}
	               , SYSDATE
	               )

mapper.update:
    UPDATE TPAHALFSHIPINFO /* pahalfcommon.xml : pahalfcommon.updateTpaHalfShipInfo by PaHalfCommonController*/
	   SET PA_SHIP_COST_CODE 	= #{paShipCostCode  , jdbcType=VARCHAR}
      	 , TRANS_DATE	  		= SYSDATE
      	 , TRANS_TARGET_YN      = '0'
      	 , MODIFY_DATE     		= SYSDATE 
	 WHERE ENTP_CODE     		= #{entpCode			, jdbcType=VARCHAR}
	   AND PA_CODE				= #{paCode				, jdbcType=VARCHAR}
	   AND SHIP_MAN_SEQ   		= #{shipManSeq			, jdbcType=VARCHAR}
	   AND RETURN_MAN_SEQ 		= #{returnManSeq		, jdbcType=VARCHAR}
	   AND SHIP_COST_CODE		= #{shipCostCode		, jdbcType=VARCHAR}
	   AND NO_SHIP_JEJU_ISLAND  = #{noShipJejuIsland	, jdbcType=VARCHAR}

mapper.select:
    	SELECT /* pahalfcommon.xml : pahalfcommon.selectPaShipCostCode by PaHalfCommonController */		
    		   HS.PA_SHIP_COST_CODE
      	  FROM TPAHALFSHIPINFO HS
    	 WHERE HS.ENTP_CODE  	 		= #{entpCode			, jdbcType=VARCHAR} 
    	   AND HS.SHIP_MAN_SEQ   		= #{shipManSeq			, jdbcType=VARCHAR}
    	   AND HS.RETURN_MAN_SEQ 		= #{returnManSeq		, jdbcType=VARCHAR}
    	   AND HS.SHIP_COST_CODE 		= #{shipCostCode		, jdbcType=VARCHAR}
		   AND HS.PA_CODE		 		= #{paCode				, jdbcType=VARCHAR}
		   AND HS.NO_SHIP_JEJU_ISLAND	= #{noShipJejuIsland	, jdbcType=VARCHAR}
    	   AND HS.PA_SHIP_COST_CODE IS NOT NULL

	SELECT /* pahalfcommon.xml : pahalfcommon.selectNullShipCostCode by PaHalfCommonController */		
		   COUNT(1) AS CNT
  	  FROM TPAHALFSHIPINFO HS
	 WHERE HS.ENTP_CODE  	 		= #{entpCode			, jdbcType=VARCHAR} 
	   AND HS.SHIP_MAN_SEQ   		= #{shipManSeq			, jdbcType=VARCHAR}
	   AND HS.RETURN_MAN_SEQ 		= #{returnManSeq		, jdbcType=VARCHAR}
	   AND HS.SHIP_COST_CODE 		= #{shipCostCode		, jdbcType=VARCHAR}
	   AND HS.NO_SHIP_JEJU_ISLAND	= #{noShipJejuIsland	, jdbcType=VARCHAR}
	   AND HS.PA_SHIP_COST_CODE IS NULL

mapper.insert:

			SELECT 
				   LTRIM(TO_CHAR(NVL(MAX(A.SEQ), 0)+1, '00000000'))
			  FROM TPAHALFSHIPINFOHISTORY A 
			 WHERE ENTP_CODE 			= #{entpCode  	 		, jdbcType=VARCHAR} 
			   AND PA_CODE 				= #{paCode		 		, jdbcType=VARCHAR}
			   AND SHIP_MAN_SEQ 		= #{shipManSeq	 		, jdbcType=VARCHAR}
			   AND RETURN_MAN_SEQ 		= #{returnManSeq 		, jdbcType=VARCHAR}
			   AND SHIP_COST_CODE		= #{shipCostCode 		, jdbcType=VARCHAR}
			   AND NO_SHIP_JEJU_ISLAND	= #{noShipJejuIsland	, jdbcType=VARCHAR}

    	INSERT INTO TPAHALFSHIPINFOHISTORY 
    		(
    			SEQ
       		  , PA_CODE
       		  , PA_SHIP_COST_CODE
       		  , FLAG
       		  , ENTP_CODE
       		  , SHIP_MAN_SEQ
       		  , RETURN_MAN_SEQ
       		  , SHIP_COST_CODE
       		  , ORD_COST
       		  , SHIP_COST_BASE_AMT
       		  , RETURN_COST
       		  , ISLAND_COST
       		  , JEJU_COST
       		  , CHANGE_COST
       		  , SHIP_COST_FLAG
       		  , POST_NO
       		  , ADDR
       		  , ADDR_DT
       		  , RET_POST_NO
       		  , RET_ADDR
       		  , RET_ADDR_DT
       		  , HP
       		  , RET_HP
       		  , INSERT_ID
       		  , INSERT_DATE
       		  , MODIFY_ID
       		  , MODIFY_DATE
       		  , NO_SHIP_JEJU_ISLAND
			) VALUES (
			   #{seq				, jdbcType=VARCHAR}
       		 , #{paCode				, jdbcType=VARCHAR}
       		 , #{paShipCostCode 	, jdbcType=VARCHAR}
       		 , DECODE(#{seq			, jdbcType=VARCHAR}, '00000001', 'I', 'U')
       		 , #{entpCode  			, jdbcType=VARCHAR}
       		 , #{shipManSeq			, jdbcType=VARCHAR}
       		 , #{returnManSeq		, jdbcType=VARCHAR}
       		 , #{shipCostCode		, jdbcType=VARCHAR}
       		 , #{ordCost			, jdbcType=NUMERIC}
       		 , #{shipCostBaseAmt	, jdbcType=NUMERIC}
       		 , #{returnCost			, jdbcType=NUMERIC}
       		 , #{islandCost			, jdbcType=NUMERIC}
       		 , #{jejuCost			, jdbcType=NUMERIC}
       		 , #{changeCost			, jdbcType=NUMERIC}
       		 , #{shipCostFlag		, jdbcType=VARCHAR}
       		 , #{postNo			    , jdbcType=VARCHAR}
       		 , #{addr				, jdbcType=VARCHAR}
       		 , #{addrDt				, jdbcType=VARCHAR}
       		 , #{retPostNo			, jdbcType=VARCHAR}
       		 , #{retAddr			, jdbcType=VARCHAR}
       		 , #{retAddrDt			, jdbcType=VARCHAR}
       		 , #{hp					, jdbcType=VARCHAR}
       		 , #{retHp				, jdbcType=VARCHAR}
       		 , 'PAHALF'
       		 , SYSDATE
       		 , 'PAHALF'
       		 , SYSDATE
       		 , #{noShipJejuIsland	, jdbcType=VARCHAR}
			)
