mapper.select:
	SELECT /* paintpcounsel.xml : paintp.paintpcounsel.selectGoodsInfoNotExistOrderNo */ 
           TG.GOODS_CODE 
      FROM TPAINTPGOODS TG
     WHERE TG.PA_CODE = #{paCode, jdbcType=VARCHAR}
       AND TG.PRD_NO = #{vendorItemId, jdbcType=VARCHAR}

mapper.update:
	UPDATE TPAQNAM /* paintpcounsel.xml : paintp.paintpcounsel.updatePaQnaTrans */
       SET TRANS_YN = '1'
         , PROC_GB = '40'
         , TRANS_DATE = #{modifyDate, jdbcType=TIMESTAMP}
         , MODIFY_ID = #{modifyId, jdbcType=VARCHAR}
         , MODIFY_DATE = #{modifyDate, jdbcType=TIMESTAMP}
     WHERE PA_COUNSEL_SEQ  = #{paCounselSeq, jdbcType=VARCHAR}

