mapper.update:
UPDATE /* paltoncommon.xml : palton.paltoncommon.updatePaltonEntpSlip */
       TPAENTPSLIP
   SET TRANS_TARGET_YN = #{transTargetYn, jdbcType=VARCHAR}
     , MODIFY_DATE = #{modifyDate, jdbcType=TIMESTAMP}
     , MODIFY_ID = #{modifyId, jdbcType=VARCHAR}
 WHERE ENTP_CODE = #{entpCode, jdbcType=VARCHAR}
   AND PA_CODE = #{paCode, jdbcType=VARCHAR}
   AND PA_ADDR_SEQ = #{paAddrSeq, jdbcType=VARCHAR}

    	UPDATE /* paltoncommon.xml : palton.paltoncommon.updatePaLtonCustShipCost by PaLtonCommonController-entpCustShipCostUpdate */
		       TPACUSTSHIPCOST
		   SET TRANS_TARGET_YN 		= '0'

    		 , GROUP_CODE			= #{delvCostPlcNo,	jdbcType=VARCHAR}

			 , MODIFY_ID        	= #{modifyId, 		jdbcType=VARCHAR}
		     , MODIFY_DATE     		= #{modifyDate,		jdbcType=TIMESTAMP}
         WHERE ENTP_CODE      	= #{entpCode,		jdbcType=VARCHAR}
           AND PA_CODE        	= #{paCode,			jdbcType=VARCHAR}
           AND SHIP_COST_CODE 	= #{shipCostCode,	jdbcType=VARCHAR}

    	UPDATE /* paltoncommon.xml : palton.paltoncommon.updatePaLtonAddShipCost by PaLtonCommonController-entpCustShipCostUpdate */
		       TPALTONADDSHIPCOST
		   SET DV_CST_POL_NO= #{delvCostPlcNo,jdbcType=VARCHAR}
         WHERE PA_CODE= #{paCode,jdbcType=VARCHAR}
           AND ISLAND_COST= #{islandCost,jdbcType=NUMERIC}
           AND JEJU_COST= #{jejuCost,jdbcType=NUMERIC}

