mapper.update:
UPDATE TPAORDERM /* panaverclaim.xml : panaver.claim.updatePaOrdermResult */
   SET API_RESULT_CODE = #{apiResultCode , jdbcType=VARCHAR}
     , API_RESULT_MESSAGE = #{apiResultMessage , jdbcType=VARCHAR}

     , PA_DO_FLAG = #{paDoFlag , jdbcType=VARCHAR}
     , PROC_DATE = #{procDate , jdbcType=TIMESTAMP}

     , MODIFY_DATE = SYSDATE
 WHERE 1=1


    AND MAPPING_SEQ = #{mappingSeq , jdbcType=VARCHAR} 


    AND PA_ORDER_NO  = #{paOrderNo , jdbcType=VARCHAR}
    AND PA_ORDER_SEQ = #{paOrderSeq , jdbcType=VARCHAR}
    AND PA_CODE = #{paCode , jdbcType=VARCHAR}

UPDATE TPAORDERM /* panaverclaim.xml : panaver.claim.updatePaOrdermHoldYn */
   SET API_RESULT_CODE = #{apiResultCode , jdbcType=VARCHAR}
     , API_RESULT_MESSAGE = #{apiResultMessage , jdbcType=VARCHAR}

     , PA_HOLD_YN = #{paHoldYn , jdbcType=VARCHAR}

 WHERE MAPPING_SEQ = #{mappingSeq , jdbcType=VARCHAR}

mapper.select:
SELECT /* panaverclaim.xml : panaver.claim.selectReturnClaimTargetList */ PM.PA_ORDER_NO AS ORDER_ID , PM.PA_ORDER_SEQ AS PRODUCT_ORDER_ID , PM.PA_CLAIM_NO AS CLAIM_ID , PM.PA_ORDER_GB FROM TPAORDERM PM WHERE PM.PA_CODE = '41' AND PM.PA_ORDER_GB = '30' AND PM.CREATE_YN = '0' AND PM.PRE_CANCEL_YN = '0' AND PM.INSERT_DATE > TRUNC(SYSDATE) - 15 AND PM.PA_ORDER_NO = #{orderID, jdbcType=VARCHAR} AND PM.PA_ORDER_SEQ = #{productOrderID, jdbcType=VARCHAR}
UNION ALL
SELECT PM.PA_ORDER_NO AS ORDER_ID , PM.PA_ORDER_SEQ AS PRODUCT_ORDER_ID , PM.PA_CLAIM_NO AS CLAIM_ID , PM.PA_ORDER_GB FROM TPAORDERM PM WHERE PM.PA_CODE = '41' AND PM.PA_ORDER_GB = '20' AND PM.OUT_BEF_CLAIM_GB = '1' AND PM.CREATE_YN = '0' AND PM.PRE_CANCEL_YN = '0' AND PM.INSERT_DATE > TRUNC(SYSDATE) - 15 AND PM.PA_ORDER_NO = #{orderID, jdbcType=VARCHAR} AND PM.PA_ORDER_SEQ = #{productOrderID, jdbcType=VARCHAR} AND EXISTS (SELECT 1 FROM TPANAVERCLAIMLIST CL WHERE CL.PRODUCT_ORDER_ID = #{productOrderID, jdbcType=VARCHAR} AND CL.CLAIM_STATUS IN ('02', '41'))

SELECT /* panaverclaim.xml : panaver.claim.selectReturnCancelTargetDtList */ PM.MAPPING_SEQ , OPM.ORDER_NO , OPM.ORDER_G_SEQ , OPM.ORDER_W_SEQ , PM.PA_PROC_QTY , NC.CLAIM_REASON_CODE AS CANCEL_CODE , OPM.PRE_CANCEL_YN FROM TPAORDERM PM /*반품취소건*/ INNER JOIN TPAORDERM OPM /*원 반품건*/ ON PM.PA_ORDER_NO = OPM.PA_ORDER_NO AND PM.PA_ORDER_SEQ = OPM.PA_ORDER_SEQ AND PM.PA_CLAIM_NO = OPM.PA_CLAIM_NO AND PM.PA_CODE = OPM.PA_CODE INNER JOIN TPANAVERORDERLIST PC ON PM.PA_ORDER_NO = PC.ORDER_ID AND PM.PA_ORDER_SEQ = PC.PRODUCT_ORDER_ID INNER JOIN TPANAVERCLAIMLIST NC ON PC.CLAIM_SEQ = NC.CLAIM_SEQ WHERE NC.CLAIM_STATUS = '14' AND PM.PA_ORDER_GB = '31' AND PM.OUT_BEF_CLAIM_GB = '0' AND PM.CREATE_YN = '0' AND PM.PRE_CANCEL_YN = '0' AND PM.INSERT_DATE > TRUNC(SYSDATE) - 5 AND OPM.PA_ORDER_GB = '30' AND OPM.CREATE_YN = '1' AND PM.PA_CODE = '41' AND PM.PA_ORDER_NO = #{orderID, jdbcType=VARCHAR} AND PM.PA_ORDER_SEQ = #{productOrderID, jdbcType=VARCHAR} AND PM.PA_CLAIM_NO = #{claimID, jdbcType=VARCHAR}
UNION ALL
SELECT PM.MAPPING_SEQ , '' AS ORDER_NO , '' AS ORDER_G_SEQ , '' AS ORDER_W_SEQ , '' AS PA_PROC_QTY , '' AS CANCEL_CODE , '1' AS PRE_CANCEL_YN FROM TPAORDERM PM WHERE PM.PA_ORDER_GB = '31' AND PM.CREATE_YN = '0' AND PM.PRE_CANCEL_YN = '0' AND PM.PA_CODE = '41' AND PM.PA_ORDER_NO = #{orderID, jdbcType=VARCHAR} AND PM.PA_ORDER_SEQ = #{productOrderID, jdbcType=VARCHAR} AND PM.PA_CLAIM_NO = #{claimID, jdbcType=VARCHAR} AND NOT EXISTS (SELECT 1 FROM TPAORDERM PMC WHERE PM.PA_ORDER_NO = PMC.PA_ORDER_NO AND PM.PA_ORDER_SEQ = PMC.PA_ORDER_SEQ AND PM.PA_CLAIM_NO = PMC.PA_CLAIM_NO AND PMC.PA_ORDER_GB ='30')
