mapper.select:
SELECT /* panavergoods.xml : panaver.panavergoods.selectPaPromoTarget by PaNaverGoodsController */ PROC_GB , PROMO_NO , PA_CODE , GOODS_CODE , SEQ , TRANS_DATE , DO_COST , OWN_COST AS DO_OWN_COST , ENTP_COST AS DO_ENTP_COST , AMT_RATE_FLAG , DO_RATE , DO_AMT , STANDARD_APP_AMT , LIMIT_AMT , INSERT_DATE , ALCOUT_PROMO_YN , PA_GROUP_CODE FROM ( SELECT PPMT.PROC_GB , PPMT.PROMO_NO , PPMT.PA_CODE , PPMT.GOODS_CODE , PPMT.SEQ , TO_CHAR(PPMT.TRANS_DATE, 'yyyy/MM/dd HH:mm:ss') AS TRANS_DATE , PPMT.DO_AMT AS DO_COST , PPMT.OWN_COST , PPMT.DO_RATE , PPMT.AMT_RATE_FLAG , PPMT.ENTP_COST , PPMT.INSERT_DATE , PPMT.STANDARD_APP_AMT , PPMT.DO_AMT , PPMT.LIMIT_AMT , PM.ALCOUT_PROMO_YN , PPMT.PA_GROUP_CODE FROM TPAPROMOTARGET PPMT , TPROMOM PM , TPPROMOTARGET PT WHERE PPMT.USE_CODE = '00' AND PT.PROMO_NO = PM.PROMO_NO AND PT.GOODS_CODE = PPMT.GOODS_CODE AND PT.GIFT_AMT = DECODE(PPMT.AMT_RATE_FLAG ,1, PPMT.DO_AMT , PPMT.DO_RATE) AND SYSDATE BETWEEN PPMT.PROMO_BDATE AND PPMT.PROMO_EDATE AND SYSDATE BETWEEN PM.PROMO_BDATE AND PM.PROMO_EDATE AND PPMT.GOODS_CODE = #{goodsCode , jdbcType=VARCHAR} AND PPMT.PA_CODE = #{paCode , jdbcType=VARCHAR}
AND PPMT.PROMO_NO = #{promoNo , jdbcType=VARCHAR}
AND PPMT.PROMO_NO = PM.PROMO_NO AND PM.COUPON_YN = '1' AND PM.IMMEDIATELY_YN = '1' AND NVL(PM.ALCOUT_PROMO_YN, '0') = #{alcoutPromoYn , jdbcType=VARCHAR} AND PROC_GB != 'D' AND PPMT.ROWID = (SELECT /*+INDEX_DESC (PTT IDX_TPAPROMOTARGET_02)*/ ROWID FROM TPAPROMOTARGET PTT WHERE PTT.PROMO_NO = PPMT.PROMO_NO AND PTT.PA_CODE = PPMT.PA_CODE AND PTT.GOODS_CODE = PPMT.GOODS_CODE AND PTT.PROC_GB != 'D' AND PTT.USE_CODE = '00' AND PTT.DO_AMT > 0 AND ROWNUM = 1 ) AND NOT EXISTS ( SELECT 'X' FROM TPAPROMOTARGET PPMT3 WHERE PPMT3.PROMO_NO = PPMT.PROMO_NO AND PPMT3.PA_CODE = PPMT.PA_CODE AND PPMT3.GOODS_CODE = PPMT.GOODS_CODE AND PPMT3.INSERT_DATE > PPMT.INSERT_DATE AND PPMT3.PROC_GB = 'D') AND NOT EXISTS ( SELECT 'X' FROM TPAPROMOEXCEPTENTP PEE INNER JOIN TGOODS GD ON GD.ENTP_CODE = PEE.ENTP_CODE AND GD.GOODS_CODE = PPMT.GOODS_CODE LEFT OUTER JOIN TPAPROMOEXCEPTBRAND PEB ON PEE.ENTP_CODE = PEB.ENTP_CODE AND PEB.USE_YN = '1' AND PEB.BRAND_CODE = GD.BRAND_CODE WHERE (INSTR(PEE.PA_GROUP_CODE, '04') > 0 OR PEE.PA_GROUP_CODE_ALL_YN = 1) AND PEE.USE_YN = '1' AND ((PEE.ALL_BRAND_YN = '1' AND SYSDATE BETWEEN PEE.PA_EXCEPT_BDATE AND PEE.PA_EXCEPT_EDATE) OR (PEE.ALL_BRAND_YN = '0' AND SYSDATE BETWEEN PEB.PA_EXCEPT_BDATE AND PEB.PA_EXCEPT_EDATE)) ) AND NOT EXISTS ( SELECT 'X' FROM TPAPROMOTARGETEXCEPT TPE , TPAGOODSPRICE PGP WHERE TPE.TARGET_CODE = PPMT.GOODS_CODE AND TPE.USE_YN = '1' AND (INSTR(TPE.PA_GROUP_CODE, '04' ) > 0 OR TPE.PA_GROUP_CODE_ALL_YN = 1) AND SYSDATE BETWEEN TPE.PA_EXCEPT_BDATE AND TPE.PA_EXCEPT_EDATE AND TPE.TARGET_GB = '00' AND PGP.ROWID = ( SELECT /*+ INDEX_DESC (GP PK_TPAGOODSPRICE)*/ GP.ROWID FROM TPAGOODSPRICE GP WHERE GP.GOODS_CODE = PPMT.GOODS_CODE AND GP.APPLY_DATE < SYSDATE AND GP.PA_CODE = PPMT.PA_CODE AND ROWNUM = 1) AND (PGP.SALE_PRICE - PGP.DC_AMT - PGP.LUMP_SUM_DC_AMT - PPMT.DO_AMT) >= TPE.PA_EXCEPT_MARGIN ) ORDER BY PPMT.DO_AMT DESC ) WHERE ROWNUM = 1

